import logo from "./logo.svg";
import "./App.css";
import React from "react";
import web3 from "./web3";
import lottery from "./lottery";

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = { manager: '' };
  }

  // component renders --> componentDidMount() is called --> call method on contract --> set data on 'state' 
  async componentDidMount() {
    const manager = lottery.methods.manager().call(); /// no need to specify from field as metamask has defaukt account already set
    this.setState({ manager });
    console.log("App component is mounted");
  }

  render() {
    // console.log("App component is rendered");
    // web3.eth.getAccounts().then(console.log);
    // console.log(web3.version);
    return (
    <div>
      <h2>Lottery Contract</h2>
      <p>This contract is managed by {this.state.manager} </p>
    </div>
    );
  }
}
export default App;
